sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	//Ezzel a változóval megkapjuk a What needed mező értékét
	var whatneeded = g_form.getValue('u_what_needed');

	//Ezzel a paranccsal megtisztítjuk az opcióktól a lenyíló mezőt
	g_form.clearOptions('U_what_needed');

	//Ha a Request type field az hr, akkor adjuk be a két HR lehetőséget és az egyéb opciót
	//Az opciókat elő tudom szedni, ha a What needed-re jobb gombbal kattintok, és megnézem a Show choice list menüt

	if(newValue == 'hr'){
		g_form.addOption('u_whatneeded','hr1','Human Resources 1');
		g_form.addOption('u_whatneeded','hr2','Human Resources 2');
		g_form.addOption('u_whatneeded','other','Other');	
	}

	//Ha a Request type field facilities, akkor adjuk be a két facility lehetőséget és az egyéb opciót

	if(newValue == 'facilities'){
		g_form.addOption('u_whatneeded','facilities1','Facilities 1');
		g_form.addOption('u_whatneeded','facilities2','Facilities 2');
		g_form.addOption('u_whatneeded','other','Other');
	}

	//Ha a Request type field az legal, akkor adjuk be a két legal lehetőséget és az egyéb opciót

	if(newValue == 'legal'){
		g_form.addOption('u_whatneeded','legal1','Legal 1');
		g_form.addOption('u_whatneeded','legal2','Legal 2');
		g_form.addOption('u_whatneeded','other','Other');
	}
